<?phprequire_once '../../config/db_connect.php';$validator = array('success' => false, 'kkmmapel' => array(), 'kkmsekolah' => array(), 'kkmkelas' => array());$kelas=substr($_REQUEST['kelas'],0,1);$tapel=$_REQUEST['tapel'];$mapel=$_REQUEST['mapel'];$sql2 = "select * from kkm where kelas='$kelas' AND tapel='$tapel' AND mapel='$mapel'";$query2 = $connect->query($sql2);$cks2 = $query2->fetch_assoc();$kkmmapel=$cks2['nilai'];$sql12 = "select MIN(nilai) as sekolah from kkm where tapel='$tapel'";$query12 = $connect->query($sql12);$cks1 = $query12->fetch_assoc();$kkmsekolah=$cks1['sekolah'];$sql12 = "select MIN(nilai) as minimal from kkm where kelas='$kelas' AND tapel='$tapel'";$query12 = $connect->query($sql12);$cks3 = $query12->fetch_assoc();$kkmkelas=$cks3['minimal'];$validator['success'] = true;$validator['kkmsekolah'] = $kkmsekolah;$validator['kkmmapel'] = number_format($kkmmapel,0);$validator['kkmkelas'] = $kkmkelas;echo json_encode($validator);?>