<?php require_once '../../config/config.php';require_once '../../config/db_connect.php';$output = array('success' => false, 'messages' => array());$tema = $_POST['tema'];$sql = "DELETE from tema where id_tema='$tema'";$query = $connect->query($sql);if($query === TRUE) {	$output['success'] = true;	$output['messages'] = 'Tema Berhasil dihapus';} else {	$output['success'] = false;	$output['messages'] = 'Error saat mencoba menghapus data siswa';}// close database connection$connect->close();echo json_encode($output);