<?php require_once '../../config/db_connect.php';$output = array('success' => false, 'messages' => array());$memberId = $_POST['member_id'];$sql = "DELETE from tp where id_tp= {$memberId}";$query = $connect->query($sql);if($query === TRUE) {	$output['success'] = true;	$output['messages'] = 'Tujuan Pembelajaran Berhasil dihapus';} else {	$output['success'] = false;	$output['messages'] = 'Error saat mencoba menghapus Tujuan Pembelajaran';}// close database connection$connect->close();echo json_encode($output);