<?php require_once '../../config/config.php';require_once '../../config/db_connect.php';$output = array('success' => false, 'messages' => array(), 'nama' => array());$idr = $_POST['member_id'];$cek = $connect->query("select * from form_data where id='$idr'")->num_rows;if($cek>0){	$info = $connect->query("select * from form_data where id='$idr'")->fetch_assoc();	$didown=$info['download'];	$lagi=$didown+1;	$sql2 = "UPDATE form_data SET download='$lagi' where id='$idr'";	$query2 = $connect->query($sql2);	$output['success'] = true;	$output['messages'] = 'Dokumen Berhasil diunduh';	$output['nama'] = $info['file_names'];}else{	$output['success'] = false;	$output['messages'] = 'Dokumen tidak ada!';}// close database connection$connect->close();echo json_encode($output);