<?php require_once '../../config/db_connect.php';$output = array('success' => false, 'messages' => array());$memberId = $_POST['member_id'];$sql = "DELETE from anak where id_anak= {$memberId}";$query = $connect->query($sql);if($query === TRUE) {	$output['success'] = true;	$output['messages'] = 'Data Anak Berhasil dihapus';} else {	$output['success'] = false;	$output['messages'] = 'Error saat mencoba menghapus Data Anak';}// close database connection$connect->close();echo json_encode($output);