<?php require_once '../../config/config.php';require_once '../../config/db_connect.php';$output = array('success' => false, 'messages' => array());$tema = $_POST['tema'];$sql = "DELETE from id_pegawai where id='$tema'";$query = $connect->query($sql);if($query === TRUE) {	$output['success'] = true;	$output['messages'] = 'ID Pegawai Berhasil dihapus';} else {	$output['success'] = false;	$output['messages'] = 'Error saat mencoba menghapus ID Pegawai';}// close database connection$connect->close();echo json_encode($output);