<?php require_once '../../config/db_connect.php';$output = array('success' => false, 'messages' => array());$memberId = $_POST['member_id'];$sql = "DELETE from riwayat_pendidikan where id_riwayat= {$memberId}";$query = $connect->query($sql);if($query === TRUE) {	$output['success'] = true;	$output['messages'] = 'Riwayat Pendidikan Berhasil dihapus';} else {	$output['success'] = false;	$output['messages'] = 'Error saat mencoba menghapus Riwayat Pendidikan';}// close database connection$connect->close();echo json_encode($output);