<?phprequire_once '../../config/config.php';require_once '../../config/db_connect.php';
$idp=$_POST['rowid'];$hariini=$_POST['tanggal'];$nsb=$connect->query("select * from id_pegawai where ptk_id='$idp'")->fetch_assoc();$ida=$nsb['pegawai_id'];$sql2 = "SELECT *,min(left(RIGHT(tanggal, 8), 5)) jam1,MAX(left(right(tanggal, 8), 5)) jam2,if(LEAST(12600,trim(TIME_TO_SEC(TIMEDIFF(min(left(RIGHT(tanggal, 8), 5)), '07:00:00'))))>0,LEAST(12600,trim(TIME_TO_SEC(TIMEDIFF(min(left(RIGHT(tanggal, 8), 5)), '07:00:00'))))/60,'') diff1,if(LEAST(14400,trim(TIME_TO_SEC(TIMEDIFF('15:45:00', MAX(left(right(tanggal, 8), 5))))))>0,LEAST(14400,trim(TIME_TO_SEC(TIMEDIFF('15:45:00', MAX(left(right(tanggal, 8), 5))))))/60,'') diff2 FROM absensi_ptk where date(tanggal)='$hariini' and pegawai_id='$ida' group by pegawai_id";$jabs=$connect->query($sql2)->fetch_assoc();?><div class="col-lg-3">													<div class="plan most-popular">														<h3>															Masuk															<em class="desc">Sekolah</em>															<span><?=$jabs['jam1'];?></span>														</h3>													</div>												</div>												<div class="col-lg-3">													<div class="plan most-popular">														<h3>															Keluar															<em class="desc">Sekolah</em>															<span><?=$jabs['jam2'];?></span>														</h3>													</div>												</div>												<div class="col-lg-3">													<div class="plan most-popular">														<h3>															Terlambat															<em class="desc">Sekolah</em>															<span><?=$jabs['diff1'];?></span>														</h3>													</div>												</div>												<div class="col-lg-3">													<div class="plan most-popular">														<h3>															Pulang Cepat															<em class="desc">Sekolah</em>															<span><?=$jabs['diff2'];?></span>														</h3>													</div>												</div>