<?phprequire_once '../../config/db_connect.php';$kelas=$_GET['kelas'];$tapel=$_GET['tapel'];$smt=$_GET['smt'];$ab=substr($kelas,0,1);$output = array('data' => array());$sql = "select * from penempatan where rombel='$kelas' and tapel='$tapel' and smt='$smt' order by nama asc";$query = $connect->query($sql);while($s=$query->fetch_assoc()) {	$idp=$s['peserta_didik_id'];	$sql1 = "select * from siswa where peserta_didik_id='$idp'";	$query1 = $connect->query($sql1);	$row1 = $query1->fetch_assoc();	$sqlp = "SELECT * FROM data_absensi WHERE peserta_didik_id='$idp' and smt='$smt' and tapel='$tapel'";	$cek = $connect->query($sqlp)->num_rows;	$pn = $connect->query($sqlp)->fetch_assoc();	$sakit=$pn['sakit'];	$ijin=$pn['ijin'];	$alfa=$pn['alfa'];	$idabsen=$pn['id'];	if($cek>0){		$tbl='		<a class="btn btn-outline-danger btn-sm me-1 mb-1" onclick="removeDataAbsensi('.$idabsen.')"><i class="fa fa-trash"></i></a>		';	}else{		$tbl='		<a class="btn btn-outline-info btn-sm me-1 mb-1" data-bs-toggle="modal" data-bs-target="#info" data-kelas="'.$kelas.'" data-tapel="'.$tapel.'" data-smt="'.$smt.'" data-pdid="'.$idp.'" id="getKes"><i class="fa fa-sync"></i></a>		';	}			$output['data'][] = array(		$tbl.' '.$row1['nama'],		$sakit,		$ijin,		$alfa	);	};// database connection close$connect->close();echo json_encode($output);