<?phprequire_once '../../config/db_connect.php';$kelas=$_GET['kelas'];$tapel=$_GET['tapel'];$smt=$_GET['smt'];$ab=substr($kelas,0,1);$output = array('data' => array());$sql = "select * from penempatan where rombel='$kelas' and tapel='$tapel' and smt='$smt' order by nama asc";$query = $connect->query($sql);while($s=$query->fetch_assoc()) {	$idp=$s['peserta_didik_id'];	$sql1 = "select * from siswa where peserta_didik_id='$idp'";	$query1 = $connect->query($sql1);	$row1 = $query1->fetch_assoc();	$sqlp = "SELECT * FROM data_ekskul WHERE peserta_didik_id='$idp' and smt='$smt' and tapel='$tapel' order by id_ekskul asc";	$cek = $connect->query($sqlp)->num_rows;	$tbl='	<a class="btn btn-outline-info btn-sm me-1 mb-1" data-bs-toggle="modal" data-bs-target="#info" data-kelas="'.$kelas.'" data-tapel="'.$tapel.'" data-smt="'.$smt.'" data-pdid="'.$idp.'" id="getKes"><i class="fa fa-plus"></i></a>	';	if($cek>0){		$queryt = $connect->query($sqlp);		$actionButton = '<div class="rich-list rich-list-bordered rich-list-action">';		while($pn=$queryt->fetch_assoc()) {			$dte=$pn['id'];			$nm=$pn['id_ekskul'];			$nme = $connect->query("SELECT * FROM ekskul WHERE id_ekskul='$nm'")->fetch_assoc();			$halo=$nme['nama_ekskul'];			$actionButton = $actionButton.'			<div class="rich-list-item">				<div class="rich-list-content">					<h4 class="rich-list-title">'.$halo.'</h4>					<span class="rich-list-subtitle">'.$pn['keterangan'].'</span>				</div>				<div class="rich-list-append">					<button onclick="removeEkskul('.$dte.')" class="btn btn-outline-danger btn-sm me-1 mb-1">						<i class="fa fa-trash"></i>					</button>				</div>			</div>			';		};			}else{		$actionButton = '';	};	$actionButton = $actionButton.'</div>';	$output['data'][] = array(		$tbl.' '.$row1['nama'],		$actionButton	);	};// database connection close$connect->close();echo json_encode($output);