<?php require_once '../../config/config.php';require_once '../../config/db_connect.php';$output = array('success' => false, 'messages' => array());$id = $_POST['member_id'];$jns = $_POST['jns']; if($jns=='k13'){	$sql = "DELETE from mapel where id_mapel='$id'";}elseif($jns=='km'){	$sql = "DELETE from mata_pelajaran where id_mapel='$id'";}elseif($jns=='dta'){	$sql = "DELETE from mapel_dta where id_mapel='$id'";}else{	$sql = "DELETE from mapel where id_mapel='$id'";};$query = $connect->query($sql);if($query === TRUE) {	$output['success'] = true;	$output['messages'] = 'Mata Pelajaran Berhasil dihapus';} else {	$output['success'] = false;	$output['messages'] = 'Error saat mencoba menghapus Mapel';}// close database connection$connect->close();echo json_encode($output);