<?php require_once '../../config/db_connect.php';//if form is submittedif($_POST) {		$validator = array('success' => false, 'messages' => array());	$idr=$_POST['ids'];	$tanggal1=$_POST['tanggal_awal'];	$tanggal2=$_POST['tanggal_akhir'];	$keterangan=$_POST['keterangan'];	$sql = "UPDATE harilibur SET tanggal_awal='$tanggal1', tanggal_akhir='$tanggal2', keterangan='$keterangan' WHERE id='$idr'";	$query = $connect->query($sql);
	if($query === TRUE) {						$validator['success'] = true;			$validator['messages'] = "Hari Libur berhasil diubah!";				} else {					$validator['success'] = false;			$validator['messages'] = "Error while adding the member information";		};	// close the database connection	$connect->close();	echo json_encode($validator);}