<?php require_once '../../config/db_connect.php';//if form is submittedif($_POST) {		$validator = array('success' => false, 'messages' => array());	$idr=$_POST['ids'];	$tanggal1=$_POST['tanggal_awal'];	$tanggal2=$_POST['tanggal_akhir'];	$jam1=$_POST['jam1'];	$jam2=$_POST['jam2'];	$sql = "UPDATE shift SET tanggal_awal='$tanggal1', tanggal_akhir='$tanggal2', masuk='$jam1', keluar='$jam2' WHERE id_shift='$idr'";	$query = $connect->query($sql);
	if($query === TRUE) {						$validator['success'] = true;			$validator['messages'] = "Shift berhasil diubah!";				} else {					$validator['success'] = false;			$validator['messages'] = "Error while adding the member information";		};	// close the database connection	$connect->close();	echo json_encode($validator);}