<?phprequire_once '../../config/db_connect.php';$tapel=$_GET['tapel'];$smt=$_GET['smt'];$output = array('data' => array());$ckkur=$connect->query("select * from rombel where nama_rombel like '%6%' and tapel='$tapel'")->fetch_assoc();$nkur=$ckkur['kurikulum'];$idkur=$connect->query("select * from kurikulum where nama_kurikulum='$nkur'")->fetch_assoc();$idk=$idkur['id_kurikulum'];if($ckkur['kurikulum']=='Kurikulum 2013'){	$sql4 = "select * from mapel order by id_mapel asc";}else{	$sql4 = "select * from mata_pelajaran order by id_mapel asc";};$sql = "select * from penempatan where rombel like '%6%' and tapel='$tapel' and smt='$smt' order by nama asc";$query = $connect->query($sql);while($s=$query->fetch_assoc()){	$idpd = $s['peserta_didik_id'];	$siswa = $connect->query("select * from siswa where peserta_didik_id='$idpd'")->fetch_assoc();	$query4 = $connect->query($sql4);		while($nk=$query4->fetch_assoc()){		$idmp=$nk['id_mapel'];		$cekn = $connect->query("select * from nilai_us where tapel='$tapel' and peserta_didik_id='$idpd' and kurikulum='$idk' and mapel='$idmp'")->num_rows;		if($cekn>0){			$m=$connect->query("select * from nilai_us where tapel='$tapel' and peserta_didik_id='$idpd' and kurikulum='$idk' and mapel='$idmp'")->fetch_assoc();			$nilaius=$m['nilai'];		}else{			$nilaius='';		}		$nt[$idmp]=$nilaius;	};	if($idk=='2'){		$output['data'][] = array(			'<a class="btn btn-outline-info btn-sm me-1 mb-1" data-bs-toggle="modal" data-bs-target="#info" data-tapel="'.$tapel.'" data-smt="'.$smt.'" data-pdid="'.$idpd.'" id="getKes"><i class="fa fa-plus"></i></a>'.$siswa['nama'],			$nt[1],$nt[2],$nt[3],$nt[4],$nt[5],$nt[6],$nt[7],$nt[8],$nt[9],$nt[10],$nt[11]		);	}else{		$output['data'][] = array(			'<a class="btn btn-outline-info btn-sm me-1 mb-1" data-bs-toggle="modal" data-bs-target="#info" data-tapel="'.$tapel.'" data-smt="'.$smt.'" data-pdid="'.$idpd.'" id="getKes"><i class="fa fa-plus"></i></a>'.$siswa['nama'],			$nt[1],$nt[2],$nt[3],$nt[4],$nt[5],$nt[6],$nt[7],$nt[8],$nt[9]		);	}};// database connection close$connect->close();echo json_encode($output);